  .modal-backdrop {
    position: fixed;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    background-color: rgba(0, 0, 0, 0.694);
    display: flex;
    justify-content: center;
    align-items: center;
  }

  .modal {
    border-radius: 10px;
    overflow-x: auto;
    position: relative;
    display: flex;
    padding: 10px;
    flex-direction: column;
    height: 500px;
    width: 25%;
    background-color: #32363E;
    border: 2px solid  rgb(25, 25, 25);
    color: rgb(29, 28, 28);
  }
  .form-control{
    width: 99%;
}
.form-control:active{
    border: 1px solid black;
}
.btn-modal{
    width: 49%;
    color: white;
}
.btn-close{
    margin-top: 25px;
    float: right;
}
.rada{
    margin-top: 70px;
}

    .modal-fade-enter,
  .modal-fade-leave-active {
    opacity: 0;
  }

  .modal-fade-enter-active,
  .modal-fade-leave-active {
    transition: opacity .5s ease
  }
</style>